let gallery=Vue.component("gallery",{template:'\n  <div class="layout">\n  <div class="gallery">\n  <div class="gallery__item" v-for="img in imgData.slice(0,n)" :style="{\'background\':  isEng ? \'url(\'+ img.image + \') no-repeat center / cover\' : \'url(../\'+ img.image + \') no-repeat center / cover\'}">\n  <button class="btn-orange btn-ripple" @click="shareImg($event, img.image)">Share</button>\n  </div>\n  </div>\n  <div class="mt-3 text-center">\n  <button @click="n -= 9" class="btn-orange btn-ripple mb-lg-0 mb-3" :style="{\'display\': (n > 9) ? \'inline-block\' : \'none\'}">Less</button>\n  <button @click="n += 9" class="btn-orange btn-ripple" :style="{\'display\': (n < imgLength) ? \'inline-block\': \'none\'}">More</button>\n  </div>\n  </div>\n  ',props:{file:{type:String,required:!0,default:""}},data:()=>({imgData:[],imgLength:0,n:9,isEng:"en"===lang}),methods:{shareImg(e,n){let t=window.location.pathname,a=t.substring(0,t.lastIndexOf("/")),i=window.location.hostname+a+"/"+n.substr(2);clipboard.writeText(i),this.changeStatus(e)},getData(){axios.get("./"+this.file).then((e=>{this.imgData=e.data.reverse(),this.imgLength=this.imgData.length}))},changeStatus(e){let n=e.currentTarget;n.innerHTML="en"===lang?"Copied!":"Â¡Copiado!",n.style.background="#0067A3",setInterval((()=>{n.innerHTML="en"===lang?"Share":"Compartir",n.style.background="#F88E2A"}),1500)}},mounted(){this.getData()}}),app=new Vue({el:".app",components:{gallery:gallery}});
