let gallery=Vue.component("gallery",{template:'\n  <div class="layout">\n  <div class="gallery">\n  <div class="gallery__item" v-for="img in imgData.slice(0,n)" :style="{\'background\': \'url(\'+ img.image + \') no-repeat center / cover\'}">\n  <button class="btn-orange btn-ripple" @click="shareImg($event, img.image)">Share</button>\n  </div>\n  </div>\n  <div class="mt-3 text-center">\n  <button @click="n -= 9" class="btn-orange btn-ripple mb-lg-0 mb-3" :style="{\'display\': (n > 9) ? \'inline-block\' : \'none\'}">Less</button>\n  <button @click="n += 9" class="btn-orange btn-ripple" :style="{\'display\': (n < imgLength) ? \'inline-block\': \'none\'}">More</button>\n  </div>\n  </div>\n  ',props:{file:{type:String,required:!0,default:""}},data:()=>({imgData:[],imgLength:0,n:9}),methods:{shareImg(e,t){let n=window.location.pathname,a=n.substring(0,n.lastIndexOf("/")),i=window.location.hostname+a+"/"+t.substr(2);clipboard.writeText(i),this.changeStatus(e)},getData(){axios.get("./"+this.file).then((e=>{this.imgData=e.data.reverse(),this.imgLength=this.imgData.length}))},changeStatus(e){let t=e.currentTarget;t.innerHTML="Copied!",t.style.background="#0067A3",setInterval((()=>{t.innerHTML="Share",t.style.background="#F88E2A"}),1500)}},mounted(){this.getData()}}),app=new Vue({el:".app",components:{gallery:gallery}});
